* {
    margin: 0;
    padding: 0;
}

li {
    list-style: none;
}

a {
    text-decoration: none;
}

button{
    border: none;
    background-color: transparent;
    cursor: pointer;
}

.header {
    background-color: black;
    color: white;

    .header_box {
        width: 1245px;
        height: 125px;
        margin: 0 auto;

        .gnb_outer {
            position: relative;
            height: 75px;
            font-size: 14px;

            .logo {
                width: 174px;
                height: 47px;
                position: absolute;
                top: 50%;
                left: 50%;
                transform: translate(-50%, -50%);

                .logo img {
                    width: 100%;
                    height: 100%;
                }
            }

            .gnb {
                display: flex;
                height: 100%;
                justify-content: flex-end;
                align-items: center;

                ul {
                    li {
                        display: inline;
                        margin-right: 17px;

                        a {
                            color: white;
                        }
                    }
                }
            }
        }//gnb_outer



        .lnb_outer {
            display: flex;
            font-size: 24px;
            justify-content: space-between;
            align-items: center;
            font-size: 24px;
            height: 50px;
            .lnb {
                ul {
                    li {
                        display: inline;
                        justify-content: space-between;
                        margin-right: 15px;
                        font-weight: bold;
                        position: relative;
                        :hover .sub_menu{
                            height: 300px;
                        }
                        .sub_menu{
                            top: 100%;
                            left: 50%;
                            transform: translateX(-50%);
                            width: 100%;
                            position: absolute;
                            overflow: hidden;
                            width: 1245px;
                            li{
                                font-size: 12px;
                                font-weight: 100;
                                top: 50px;
                                display: inline-block;
                                a{

                                }
                            }
                        }

                        a {
                            color: white;

                        }
                    }
                }
            }
            .lnb_rank {
                font-size: 14px;
            }
        }//lnb_outer
    }//header_box
}//header


.wrap_ex{
    width: 1245px;
    margin: 0 auto;
    padding-top: 90px;

    .pr_wrap{
        display: flex;
        .pr_visual{
        margin-right: 215px;
            width: 510px;
            .pr_scode{
                display: flex;
                p{
                    margin-right: 15px;
                    color: #646464;
                }
                span{
                    font-weight: bold;
                    color: #201F1F;
                }
            }

            .pr_img{
                margin-top: 15px;
                width: 510px; height: 540px;
